import React from 'react';

import './PolaroidComponent.css';
import { makeStyles } from '@material-ui/core/styles';

import defaultbear from './assets/default_logo.jpg';

const PolaroidComponent = ({ image, name, position, link }) => {
  return (
    <a href={linkedin != null ? `${linkedin}` : "https://www.linkedin.com/company/sproul-club/"} target="_blank">
    <div className="polaroid">
      <div className="imageContainer">
        {image != null ? image :
          <img
            className="default-image"
            src={defaultbear}
            alt="a light blue bear with a dark blue background"
          />
        }
      </div>
      <div className="text">
        <h1>
          {name}
        </h1>
        <h2>
          {position}
        </h2>
      </div>
    </div>
    </a>
  );
}

export default PolaroidComponent;
