import React from 'react';

import PictureButton from './../../components/PictureButton.js'
import IconButton from './../../components/IconButton.js'
import Carousel from './../../components/Carousel.js'
import RectanglePolaroid from './../../components/RectanglePolaroid.js'

import './About.css';

import s from './../../assets/spotify@4x.png';

function About() {
    var spot = <img class="image" src={s}></img>;
    return (
        <div class="about">
            <div class="centered">
                <p class="big-text"> Izzie Lau </p>
                <div class="icon-row">
                    <IconButton link="https://linkedin.com/in/izzielau" icon=<i class="fa fa-linkedin"/> />
                    <IconButton link="https://github.com/izzielau" icon=<i class="fa fa-github"/> />
                </div>
            </div>
                <p class="text"> <b> Computer Science + Cognitive Science alumni of University of California, Berkeley. </b> </p>
                <p class="text"> Currently working in machine learning in the Core Search & AI Metrics team at Microsoft! </p>
                <p class="text"> Avid learner of all things at the intersection of neuroscience, tech, or philosophy.  </p>
                <p class="text"> My ideal night in: my cloud lamp, a lavender-scented candle, and a comfy video game. </p>
                <p class="text"> ପ(๑•ᴗ•๑)ଓ ♡ </p>
                <p class="text"> </p>
                <p class="text"> Take a look at the podcasts I'm currently listening to! ↓ </p>
                <iframe class="spotify-embed" src="https://open.spotify.com/embed/playlist/3bxMxrIYazwlOmuyVsQHJo?utm_source=generator&theme=0" width="100%" height="380" frameBorder="0" allowfullscreen="" allow="autoplay; clipboard-write; encrypted-media; fullscreen; picture-in-picture" loading="lazy"></iframe>
        </div>
    );
}

export default About;
