import React from 'react';

import PictureButton from './../../components/PictureButton.js'
import IconButton from './../../components/IconButton.js'
import Carousel from './../../components/Carousel.js'
import RectanglePolaroid from './../../components/RectanglePolaroid.js'

import './About.css';

import s from './../../assets/spotify@4x.png';

function About() {
    var spot = <img class="image" src={s}></img>;
    return (
        <div class="about">
            <div class="top">
                <div class="left-container">
                    <p class="big-text"> Izzie Lau </p>
                    <p class="text"> Graduated from University of California, Berkeley in 2022. Currently a software engineer at Microsoft. </p>
                    <div class="icon-row">
                        <IconButton link="https://linkedin.com/in/izzielau" icon=<i class="fa fa-linkedin"/> />
                        <IconButton link="https://github.com/izzielau" icon=<i class="fa fa-github"/> />
                    </div>
                </div>
                <div class="right-container">
                </div>
            </div>
            <div class="bottom">
                <p class="text"> ପ(๑•ᴗ•๑)ଓ ♡ </p>
                <p class="text"> Avid learner of all things at the intersection of neuroscience, tech, or philosophy.  </p>
                <p class="text"> Ideal night in: my cloud lamp, a lavender-scented candle, and a comfy video game (while singing karaoke or taking notes on a podcast). </p>

            </div>
        </div>
    );
}

export default About;
