import React from 'react';

import PictureButton from './../../components/PictureButton.js'
import IconButton from './../../components/IconButton.js'
import Carousel from './../../components/Carousel.js'

import './About.css';
import izzie from './../../assets/izz.JPG';
import snow from './../../assets/snow.jpg';
import valo from './../../assets/valo.JPG';
import raves from './../../assets/raves.jpg';
import design from './../../assets/design.jpg';
import photo from './../../assets/pic.jpg';

const classes = ['algorithms', 'artificial intelligence', 'computational models', 'data science', 'databases', 'engineering statistics', 'industrial design', 'machine learning', 'natural language processing', 'neural networks', 'probability & risk analysis', 'product management', 'quantitative finance', 'recommendation systems in ML'];

function About() {
    return (
        <div class="about">
            <div class="top">
                <div class="left-container">
                    <p class="big-text"> IZZIE LAU </p>
                    <img class="image" src={izzie}/>
                    <div class="icon-row">
                        <IconButton link="linkedin.com/in/izzielau" icon=<i class="fa fa-linkedin"/> />
                        <IconButton link="github.com/izzielau" icon=<i class="fa fa-github"/> />
                    </div>
                </div>
                <div class="right-container">
                    <p class="text-bold">graduating from uc berkeley in spring 2022. </p>
                    <p class="text"> → majors in <i>computer science</i> & <i>cognitive science</i> </p>
                    <p class="text"> → minor in <i>data science</i> (cognition)</p>
                    <p class="text"> → certificate in <i>tech & entrepreneurship</i></p>
                    <p class="text-bold"> </p>
                    <p class="text-bold">afterwards, i'll be a full-time swe @ microsoft. </p>
                    <p class="text-bold"> </p>
                    <p class="text-bold"> here's some of my most relevant coursework!</p>
                    <Carousel tags={classes}> </Carousel>
                </div>
            </div>
            <div class="bottom">
                <p class="text-bold">and if you want to get to know me, here are my favorite hobbies!  </p>
                <div class="picture-row">
                    <img class="image" src={snow}/> Snowboarding
                    <img class="image" src={design}/>
                    <img class="image" src={raves}/>
                    <img class="image" src={photo}/>
                    <img class="image" src={valo}/>
                </div>
            </div>
        </div>
    );
}

export default About;
