import React from 'react';

import './Post.css'

import { Route, Link, BrowserRouter as Router } from 'react-router-dom'

class Post extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            name: this.props.name,
            usernameLink: "user/" + this.props.username,
            usernameTag: "@" + this.props.username,
            post: this.props.post,
        };
    }

    render() {
        return(
            <div className="Post">
                <div className="Post-name">
                    {this.props.name}
                </div>
                <div className="Post-username">
                    <Link to={this.state.usernameLink}>{this.state.usernameTag}</Link>
                </div>
                <div className="Post-spacemargin">
                </div>
                <div className="Post-context">
                    {this.props.post}
                </div>
            </div>
        );
    }
}

export default Post;
